  ecc = new ECCCommand()
  outputs.stdout = ecc.run(gs.getProperty('x_snc_hspoke.midserver'), inputs.cmd, inputs.tx.getTableName()+ ":" + inputs.tx.sys_id)

})(inputs, outputs);

