sys_variable_value.value:
(function execute(inputs, outputs) {

  var accounts = []
  inputs.accounts.query()
  while (inputs.accounts.next()) {
    accounts.push(inputs.accounts.getValue("account_id"))
  }

  outputs.json = JSON.stringify({ accounts: accounts})

})(inputs, outputs);

