sys_variable_value.value:
(function execute(inputs, outputs) {

var key = inputs.key
var memo = inputs.memo
var rsr = inputs.rsr
var sign_with = inputs.sign_with

var cmd = './hcli account create'

if (key.public_key) { cmd = cmd +  ' --key ' + key.public_key } 
if (memo) { cmd = cmd +  ' --memo ' + memo } 
if (rsr) { cmd = cmd + ' --reqsig' }

if (sign_with) {
  while (sign_with.next()) {
    cmd = cmd + ' --sign ' + sign_with.public_key
  }
}

outputs.cmd = cmd

})(inputs, outputs);

