 wipe_key.get(tx.wipe_key) 
 cmd = cmd + ' --wipekey ' + wipe_key.public_key
 }

if (tx.kyc_key) {
 kyc_key = new GlideRecord("x_snc_hspoke_key")
 kyc_key.get(tx.kyc_key)
 cmd = cmd + ' --kyckey ' + kyc_key.public_key
 }

if (tx.token_memo) {
 cmd = cmd + ' --memo ' + tx.token_memo
 }

if (tx.sign_with.toString() != "") {
 k = tx.sign_with.toString().split(',')
 for (i = 0; i < k.length; i++) {
 kk = new GlideRecord('x_snc_hspoke_key')
 kk.get(k[i])
 cmd = cmd + ' --sign ' + kk.public_key 
 }
}

//cmd = cmd + " 2>/dev/null"

return cmd"}}

sys_variable_value.value:
(function execute(inputs, outputs) {

    var tx = inputs.tx

    try {
        tx.output = inputs.cmdout

        output = JSON.parse(inputs.cmdout)

        tx.transaction_id = output.resp.transactionId
        tx.transaction_hash = output.resp.transactionHash

        tx.token_id = output.receipt.tokenId

        gr = new GlideRecord("x_snc_hspoke_token")
        gr.initialize()
        gr.name = output.receipt.tokenId
        gr.token_id = output.receipt.tokenId
        gr.insert()

        tx.token = gr.sys_id

        if (output.receipt.status == 'SUCCESS') {
            tx.status = 'completed'
            tx.hedera_status = output.receipt.status
        } else {
            throw Error("Hedera status is not success")
        }

    } catch (e) {
        tx.out_parsing_log = e.toString()
        tx.hedera_status = output.error.status
        tx.status = 'failed'
    } finally {
        tx.update() 
    }

})(inputs, outputs);

