sys_hub_input_scripts.script:
{"command":{"scriptActive":true,"script":"var tx = new GlideRecord('x_snc_hspoke_tokenassociate')
tx.get(fd_data.action_inputs.tx)

var token = new GlideRecord('x_snc_hspoke_token')
token.get(tx.token)

var account = new GlideRecord('x_snc_hspoke_account')
account.get(tx.account)

var cmd = './hcli token associate'

if (tx.token) { cmd = cmd + ' --tokenid ' + token.token_id }
if (tx.account) { cmd = cmd + ' --accountid ' + account.account_id }

k = tx.sign_with.toString().split(',')

for (i = 0; i < k.length; i++) {
 kk = new GlideRecord('x_snc_hspoke_key')
 kk.get(k[i])
 cmd = cmd + ' --sign ' + kk.public_key 
}

//cmd = cmd + " 2>/dev/null"

return cmd"}}

