sys_variable_value.value:
(function execute(inputs, outputs) {

var tx = inputs.tx

var key = new GlideRecord("x_snc_hspoke_key")
key.get(tx.key)

var cmd = 'cd ' + gs.getProperty('x_snc_hspoke.hcli_home') + ' && node hcli account create'

if (key.public_key) { cmd = cmd +  ' --key ' + key.public_key } 
if (tx.memo) { cmd = cmd +  ' --memo ' + tx.memo } 
if (tx.receiver_signature_required) { cmd = cmd + ' --reqsig' }

outputs.cmd = cmd

})(inputs, outputs);

