(function execute(inputs, outputs) {

  ecc = new ECCCommand()
  outputs.stdout = ecc.runAsync(gs.getProperty('x_snc_hspoke.midserver'), inputs.cmd, inputs.tx.getTableName()+ ":" + inputs.tx.sys_id)

})(inputs, outputs);

