sys_variable_value.value:
(function execute(inputs, outputs) {

  cmd = 'cd ' + gs.getProperty('x_snc_hspoke.hcli_home') + ' && node hcli account balances '

  if (inputs.accounts) {
  while (inputs.accounts.next()) {
    cmd = cmd + ' --accountid ' + inputs.accounts.getValue("account_id")
  }   
}

  outputs.cmd = cmd

})(inputs, outputs);

