sys_variable_value.value:
(function execute(inputs, outputs) {

var cmd = './hcli token mint'

if (inputs.token.token_id) { cmd = cmd + ' --tokenid ' + inputs.token.token_id }
if (inputs.amount) { cmd = cmd +  ' --amount ' + inputs.amount }

for (i=0; i < inputs.metadata.length; i++) { 
  cmd = cmd + " --metadata '" + inputs.metadata[i] + "'"
}

if (inputs.sign_with) {
  while (inputs.sign_with.next()) {
    cmd = cmd + ' --sign ' + inputs.sign_with.public_key
  }
}

outputs.cmd = cmd

})(inputs, outputs);

