sys_variable_value.value:
(function execute(inputs, outputs) {

var serials = inputs.serials.toString().split(',')

gs.info(serials.length)

for (i = 0; i < serials.length; i++) {
  gs.info("step " + i)
  var nft = new GlideRecord('x_snc_hspoke_nft')
  nft.initialize()
  nft.serial = serials[i]
  nft.name = serials[i] + "@" + inputs.token.token_id
  nft.token = inputs.token.sys_id
  nft.insert()
}

})(inputs, outputs);

