sys_variable_value.value:
(function execute(inputs, outputs) {

  var response = JSON.parse(inputs.response)

  var fail = false

  if (!response) {
    outputs.hstatus = "NO_RESPONSE"
    fail = true
  }


  if (response.resp) {
    outputs.transaction_id = response.resp.transactionId
    outputs.transaction_hash = response.resp.transactionHash
    outputs.node_id = response.resp.nodeId
  } else {
    fail = true
  }

  if (response.receipt ) {
    outputs.hstatus = response.receipt.status
//    outputs.account_id = response.receipt.accountId
//    outputs.token_id = response.receipt.tokenId
//    outputs.serials = response.receipt.serials
//    outputs.topic_id = response.receipt.topicId
  } else {
    fail = true 
  }

  if (response.record) {
    outputs.transaction_fee = response.record.transactionFee
    outputs.consensus_timestamp = response.record.consensusTimestamp
  } else {
    fail = true
  }

  if (response.error) {
   	outputs.hstatus = response.error.status
    fail = true
  }

  outputs.fail = fail

})(inputs, outputs);

