sys_variable_value.value:
(function execute(inputs, outputs) {

var cmd = './hcli token associate'

if (inputs.account) { cmd = cmd +  ' --accountid ' + inputs.account.account_id }

if (inputs.tokens) {
  while (inputs.tokens.next()) {
    cmd = cmd + ' --tokenid ' + inputs.tokens.token_id
  }
}

if (inputs.sign_with) {
  while (inputs.sign_with.next()) {
    cmd = cmd + ' --sign ' + inputs.sign_with.public_key
  }
}


outputs.cmd = cmd

})(inputs, outputs);

