sys_variable_value.value:
(function execute(inputs, outputs) {

var cmd = './hcli token transfer' 

var transfer
if (inputs.amount.toString() != "") {
  transfer = [{
    from: inputs.from.account_id.toString(),
    to: inputs.to.account_id.toString(),
    token_id: inputs.token_id.toString(),
    amount: inputs.amount.toString()
  }]
}

if (inputs.serial.toString() != "") {
  transfer = [{
    from: inputs.from.account_id.toString(),
    to: inputs.account_id.toString(),
    token_id: inputs.token_id.token_id.toString(),
    serial: inputs.nft.serial.toString()
  }]
}

cmd = cmd +  ' --transfers \'' + JSON.stringify(transfer) + '\''

if (sign_with) {
  while (sign_with.next()) {
    cmd = cmd + ' --sign ' + sign_with.public_key
  }
}

outputs.cmd = cmd

})(inputs, outputs);

