sys_variable_value.value:
(function execute(inputs, outputs) {

tx = inputs.tx

from = new GlideRecord('x_snc_hspoke_account')
from.get(tx.from)

to = new GlideRecord('x_snc_hspoke_account')
to.get(tx.to)

token = new GlideRecord('x_snc_hspoke_token')
token.get(tx.token)

var cmd = 'cd ' + gs.getProperty('x_snc_hspoke.hcli_home') + ' && node hcli token transfer' 

var transfer
if (tx.amount.toString() != "") {
  transfer = [{
    from: from.account_id.toString(),
    to: to.account_id.toString(),
    token_id: token.token_id.toString(),
    amount: tx.amount.toString()
  }]
}

if (tx.serial.toString() != "") {
  transfer = [{
    from: from.account_id.toString(),
    to: to.account_id.toString(),
    token_id: token.token_id.toString(),
    serial: tx.serial.toString()
  }]
}

cmd = cmd +  ' --transfers ' + JSON.stringify(JSON.stringify(transfer))
//cmd = cmd +  ' --transfers \'' + JSON.stringify(transfer) + '\''
//cmd = cmd +  ' --transfers &apos;' + JSON.stringify(transfer) + '&apos;' //it works

if (tx.sign_with.toString() != "") {
  k = tx.sign_with.toString().split(',')
  for (i = 0; i < k.length; i++) {
    kk = new GlideRecord('x_snc_hspoke_key')
    kk.get(k[i])
    cmd = cmd + ' --sign ' + kk.public_key 
  }
}

outputs.cmd = cmd

})(inputs, outputs);

