sys_hub_input_scripts.script:
{"command":{"scriptActive":true,"script":"var tx = new GlideRecord('x_snc_hspoke_tokenmint')
tx.get(fd_data.action_inputs.tx)

var token = new GlideRecord('x_snc_hspoke_token')
token.get(tx.token)

var cmd = './hcli token mint'

if (token.token_id) { cmd = cmd + ' --tokenid ' + token.token_id }
if (tx.amount) { cmd = cmd + ' --amount ' + tx.amount }
if (tx.metadata) { cmd = cmd + " --metadata '" + tx.metadata +"'" }

k = tx.sign_with.toString().split(',')

for (i = 0; i < k.length; i++) {
 kk = new GlideRecord('x_snc_hspoke_key')
 kk.get(k[i])
 cmd = cmd + ' --sign ' + kk.public_key 
}

return cmd"}}

