sys_variable_value.value:
(function execute(inputs, outputs) {

    var tx = inputs.tx
	 var stdout = tx.output;

    try {
        output = JSON.parse(stdout)

        tx.transaction_id = output.resp.transactionId
        tx.transaction_hash = output.resp.transactionHash
        tx.transaction_fee = output.record.transactionFee

      //specialized part - start
        tx.token_id = output.receipt.tokenId

        gr = new GlideRecord("x_snc_hspoke_token")
        gr.initialize()
        gr.name = output.receipt.tokenId
        gr.token_id = output.receipt.tokenId
        gr.insert()

        tx.token = gr.sys_id
      //specialized part - start

        if (output.receipt.status == 'SUCCESS') {
            tx.status = 'completed'
            tx.hedera_status = output.receipt.status
        } else {
            throw Error("Hedera status is not success")
        }

    } catch (e) {
        tx.out_parsing_log = e.toString()
        tx.hedera_status = output.error.status
        tx.status = 'failed'
    } finally {
        tx.update() 
    }

})(inputs, outputs);

