sys_variable_value.value:
(function execute(inputs, outputs) {

tx = inputs.tx

var cmd = 'cd ' + gs.getProperty('x_snc_hspoke.hcli_home') + ' && node hcli token create'

if (tx.token_name) { cmd = cmd +  ' --name ' + tx.token_name }
if (tx.type) { cmd = cmd +  ' --type ' + tx.type }
if (tx.symbol) { cmd = cmd +  ' --symbol ' + tx.symbol }
if (tx.initial_supply) { cmd = cmd +  ' --supply ' + tx.initial_supply }
if (tx.decimals) { cmd = cmd +  ' --decimals ' + tx.decimals }

if (tx.treasury_account) {
  treasury_account = new GlideRecord("x_snc_hspoke_account")
  treasury_account.get(tx.treasury_account)
  cmd = cmd +  ' --treasury ' + treasury_account.account_id
  }

if (tx.max_supply) { cmd = cmd + ' --maxsupply ' + tx.max_supply }
if (tx.supply_key) {
  supply_key = new GlideRecord("x_snc_hspoke_key")
  supply_key.get(tx.supply_key)
  cmd = cmd + ' --supplykey ' + supply_key.public_key
  }

if (tx.admin_key) {
  admin_key = new GlideRecord("x_snc_hspoke_key")
  admin_key.get(tx.admin_key)
  cmd = cmd + ' --adminkey ' + admin_key.public_key
  }

if (tx.freeze_key) {
  freeze_key = new GlideRecord("x_snc_hspoke_key")
  freeze_key.get(tx.freeze_key)
  cmd = cmd + ' --freezekey ' + freeze_key.public_key
  }

if (tx.pause_key) {
  pause_key = new GlideRecord("x_snc_hspoke_key")
  pause_key.get(tx.pause_key)
  cmd = cmd + ' --pausekey ' + pause_key.public_key
  }

if (tx.wipe_key) {
  wipe_key = new GlideRecord("x_snc_hspoke_key")
  wipe_key.get(tx.wipe_key) 
  cmd = cmd + ' --wipekey ' + wipe_key.public_key
  }

if (tx.kyc_key) {
  kyc_key = new GlideRecord("x_snc_hspoke_key")
  kyc_key.get(tx.kyc_key)
  cmd = cmd + ' --kyckey ' + kyc_key.public_key
  }

if (tx.token_memo) {
  cmd = cmd + ' --memo ' + tx.token_memo
  }

if (tx.sign_with.toString() != "") {
  k = tx.sign_with.toString().split(',')
  for (i = 0; i < k.length; i++) {
    kk = new GlideRecord('x_snc_hspoke_key')
    kk.get(k[i])
    cmd = cmd + ' --sign ' + kk.public_key 
  }
}

//cmd = cmd + " 2>/dev/null"

outputs.cmd = cmd

})(inputs, outputs);

